styles.css:
wp-travel-book ,
.wp-element-button,
.wpcf7-submit,
.wp-block-button__link {
    background: #1D3557!important;
    border: none !important;
    color: #fff!important;
    font-size: 15px;
    border-radius: 20px !important;
    padding: 15px 30px !important;
}

.wp-travel-book:hover ,
input.wp-element-button:hover,
.wpcf7-submit:hover,
.wp-block-button__link:hover,
input.wp-element-button:focus,
.wpcf7-submit:focus,
.wp-block-button__link:focus {
    background-color: #E63946 !important;
    color: #fff !important; 
    border: none !important; 
}



.page-numbers.current,
a:focus,
a:hover{
color: var(--wp--preset--color--secondary);
}

.wp-block-query-pagination a,
.wp-block-comment-date a,
.wp-block-comment-author-name a,
.wp-block-navigation-submenu a,
.wp-block-navigation-link a,
.wp-block-categories a,
.wp-block-post-title a,
.wp-block-site-title a{
   text-decoration: none !important;
}

.about-section-image-relative{
    z-index: 2;
    position: relative !important;
}


.testimonial-relative-post-content,
.about-relative-post-content{
z-index: 2;
    position: relative !important;
}

.wpcf7-text{
width: 100%;
height: 30px;
}

.wpcf7-textarea{
width: 100%;
}

input.wp-element-button,
.wpcf7-submit{
        padding: 10px 30px;
    background: var(--wp--preset--color--primary) !important;
    border: none;
    color: #fff;
font-size: 16px;
}
.wp-travel-booking__panel-bottom {

    z-index: 99999!important;
}
.single .wp-block-post-terms a {
    border: 1px solid;
    border-radius: 2px;
    padding: 5px;
    margin: 5px;
    text-decoration: none;
}

@media only screen and (max-width: 1000px) {
  .mobile-disable{
      display:none;
  }
}
.splide__arrow {
    border-radius: 50% !important;
   background: #1D3557 !important;
}
.splide__arrow svg{
fill: #ffffff!important;
}
.splide__pagination {
top: 330px;
}
.tour-number p{
padding: 10px 20px;
    background: #212529;
    border-radius: 20px;
}
#wptravel-block-trip-duration.wptravel-block-wrapper{
padding: 10px 20px;
    background: #212529;
    border-radius: 20px;
}


#wptravel-scroll-to-top {
    display: none !important;
}
@media(max-width: 781px){
.is-layout-flex {
flex-wrap: wrap!important;
}
}
@media only screen and (max-width: 781px) {
.wp-block-navigation__responsive-container-open:not(.always-shown) {
        display: block !important;
    }
header .wp-block-navigation__responsive-container{
display: none !important;
}
header .wp-block-navigation__responsive-container.is-menu-open{
display: block !important;
}
}


.wptravel-block-trip-search .wp-travel-search form select{
    padding: 15px 15px;
border-radius: 22px !important;
}
.wptravel-block-trip-search .wp-travel-search form input {
    padding: 15px 15px;
border-radius: 22px !important;
}
#wptravel-block-trip-duration.wptravel-block-wrapper,
.tour-number p {
    padding: 10px 20px;
    background: #E63946;
    border-radius: 20px;
color:#fff;
}
.wp-block-jetpack-subscriptions.wp-block-jetpack-subscriptions__supports-newline form input[type=email]:disabled {
    color: rgb(0 0 0)!important;
}
.img-hover-zoom img {
  object-fit: cover;
  border-radius: 20px;
  transition: transform 0.5s ease;
}
.img-hover-zoom:hover img {
  transform: scale(1.05);
}

.on-hover-shadow {
  transition: box-shadow 0.3s ease;
}
.on-hover-shadow:hover {
 background-color: #F1FAEE;
}
.itineraries .fixed-date-dropdown .dropbtn {
    color: #fff!important;
}
.itineraries .fixed-date-dropdown .dropbtn::after{ color:#ffff!important;
}


#newBookingDetails tbody td{
   border: 1px solid #b3b3b3 !important;
}


@media (max-width: 781px) {
#newsletter .wp-block-cover__background{
    background: linear-gradient(90deg,rgba(241, 250, 238, 1))!important;
}
}
.wp-block-template-part {
  position: sticky!Important;
  top: 0;
 z-index: 1000;
background-color: #fff;
}
.back-to-top-icon.right {
    z-index: 9999;
}
.wp-block-navigation:not(.has-text-color) .wp-block-navigation__responsive-container.is-menu-open {
    color: #ffffff!important;
    background: #000!important;
}
.wp-block-navigation:not(.has-text-color) .wp-block-navigation__submenu-container {
    color: #ffffff!important;
    background: #000000!important;
}

.view-box {
    border-radius: 32px!important;
    padding: 12px!important;
    background: #F1FAEE !important;  
}
.image-thumb img {
    border-radius: 20px!important;
}
.wptravel-guide-image-block {
    height: auto!important;
    width: 100%!important;
    border-radius: 20px;
}
.wptravel-travel-guide-Review{
 background: #F1FAEE !important;  
 border-radius: 20px;
}
.form-submit input {
    background-color: #1D3557 !important;
}
.view-image .offer {
    background: #E63946!important;
    border-radius: 20px!important;
}
.wptravel-block-trip-filters .wp-travel-itinerary-items .wp-travel-form-field {
    margin-bottom: 20px!important;
}
.wp-trave-price-range .ui-widget.ui-widget-content {
    top: 12px;
}
.heading-hover a:hover,
.wp-block-query-pagination a:hover,
.wptravel-guide-fullname-block a:hover,
.taxonomy-post_tag a:hover,
.taxonomy-category a:hover,
#faq .wp-collapse-open a:hover ,
.panel-title a:hover ,
.wp-block-wens-haelo-pro-icon-picker a:hover ,
.taxonomy-travel_locations a:hover ,.wp-block-post-title a:hover{
color:#E63946!important;
}
@media(max-width: 781px){.search-bar-margin{
margin-top: -50px!important;
}
}
@media(max-width: 600px){.search-bar-margin{
margin-top:-40px!important;
}
}
@media(max-width: 475px){.search-bar-margin{
margin-top:-25px!important;
}
}

@media (max-width: 1024px) {
#blog .wrap-the-columns
 {
   grid-template-columns: repeat(3, minmax(0, 1fr))!important;
}
}
@media (max-width: 781px) {#blog
.wrap-the-columns
 {
  grid-template-columns: repeat(2, minmax(0, 1fr))!important;
}
}
@media (max-width: 600px) {#blog
.wrap-the-columns
{
 grid-template-columns: repeat(1, minmax(0, 1fr))!important;
}
}


@media (max-width: 1200px) {#Guide .wrap-the-columns
 {
   grid-template-columns: repeat(3, minmax(0, 1fr))!important;
}
}
@media (max-width: 781px) { #Guide
.wrap-the-columns
 {
  grid-template-columns: repeat(2, minmax(0, 1fr))!important;
}
}
@media (max-width: 600px) { #Guide
.wrap-the-columns
{
 grid-template-columns: repeat(1, minmax(0, 1fr))!important;
}
}
.splide__arrow--prev {
    left: 3em!important;
}
.splide__arrow--next {
    right: 3em!important;
}
.single .wp-block-post-terms a {  border:none!important;
}
a:where(:not(.wp-element-button)) {
    text-decoration: none!important;
}

.fixed-date-dropdown .dropdown-content {
    background-color: #E63946!important;
    z-index: 11!important;
    border-radius: 20px;
}
.fixed-date-dropdown .dropdown-content .dropdown-list {
    color: #Fff !important;
}
.fixed-date-dropdown .dropdown-content .dropdown-.fixed-date-dropdown .dropbtnlist:hover{
 background-color: #1D3557!important;
  border-radius: 20px;
}
#shopping-cart .cart-summary { border-radius: 20px;
}
#wptravel-block-trip-sale {
border-radius: 20px;
}
.checkout-page-wrap .panel.ws-theme-timeline-block, 
.panel ws-theme-timeline-block,  .panel ws-theme-timeline-block,
.wp-travel-trip-details {
    background-color: #F1FAEE !important;
    border-radius: 20px !important;
}
.wp-travel-cart-sidebar {
    background-color: #F1FAEE!important;
}
#wptravel-block-trip-sale {
    padding: 15px 30px;
    background-color: #E63946;
    color: #ffffff;
    border-radius: 20px;
}
.fixed-date-dropdown .dropdown-content .dropdown-list:hover {
color:#000!important;
border-radius: 20px;
}
#no-wrap .is-layout-flex {
    flex-wrap: nowrap!important;
}


@media (min-width: 782px) and (max-width: 1024px) {
  #tours .wrap-the-columns {
    grid-template-columns: repeat(3, minmax(0, 1fr)) !important;
  }
}
@media (min-width: 600px) and (max-width:781px) {
  #tours .wrap-the-columns {
    grid-template-columns: repeat(2, minmax(0, 1fr)) !important;
  }
}
#tours .wp-block-post-template-is-layout-grid.wp-block-post-template-is-layout-grid.wp-block-post-template-is-layout-grid.wp-block-post-template-is-layout-grid {
    overflow: hidden;
}

@media (max-width: 1024px) and (min-width: 782px) {
    #top-category .wp-block-column {
        flex: 1 1 calc(33.333% - 16px);
        max-width: calc(33.333% - 16px);
    }
}


@media (max-width: 781px) and (min-width: 601px) {
    #top-category .wp-block-column {
        flex: 1 1 calc(50% - 16px);
        max-width: calc(50% - 16px);
    }
}

@media (max-width: 600px) {
    #top-category .wp-block-column {
        flex: 1 1 100%;
        max-width: 100%;
    }
}
.wptravel-block-trip-filters #wp-travel-filter-search-submit{
width:100%!important;
}
#wp-travel-enquiries .wp-travel-form-field input:not([type=checkbox]):not([type=radio]){
width:100%!important;
}
.wp-travel-book, .wp-element-button, .wpcf7-submit, .wp-block-button__link{
padding: 15px 30px !important;
border: none !important;
background: #1D3557 !important;
color: #fff;
border-radius: 20px !important;
}
